import React from 'react'


export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      seconds: "00",
      minutes: "00",
      isReset: false,
      isMainTimerUsed: false
    }
    this.secondsRemaining = "";
    this.intervalHandle = "";

    this.startCountDown = this.startCountDown.bind(this);
    this.tick = this.tick.bind(this);

    this.startCountDown();
  }

  tick() {
    this.setState({
      isReset: false
    })

    var min = Math.floor(this.secondsRemaining / 60);
    var sec = this.secondsRemaining - (min * 60);
    console.log(min)

    this.setState({
      minutes: min,
      seconds: sec
    })

    if (sec < 10) {
      this.setState({
        seconds: "0" + this.state.seconds,
      })
    }
    if (min < 10) {
      this.setState({
        minutes: "0" + min,
      })
    }

    if (min === 0 & sec === 0) {
      this.setState({
        ...this.state,
        isReset: true
      })

      clearInterval(this.intervalHandle);

      this.setState({
        ...this.state,
        isMainTimerUsed: true
      })

    }

    this.secondsRemaining--
  }

  startCountDown() {

    let time = this.state.isMainTimerUsed ? "01" : this.state.minutes;
    this.secondsRemaining = time * 60;
    console.log(this.state.isMainTimerUsed, this.state.minutes, this.secondsRemaining);

    this.intervalHandle = setInterval(this.tick, 1000);
  }

  render() {
    return ( <
      div >
      <
      span > Your OTP is expiring in {
        this.state.minutes
      }: {
        this.state.seconds
      } < /span> <
      br / >
      <
      button disabled = {
        !this.state.isReset
      }
      onClick = {
        this.startCountDown
      } > Resend OTP < /button> {
        /* <button onClick={this.startCountDown}>Next</button> */ } <
      /div>
    )
  }
}
