import React from 'react'

export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state={
      seconds: "00",
      minutes: "05",
      isReset: false,
      isMainTimerUsed: false
    }
    this.secondsRemaining = ""; 
    this.intervalHandle = "";

    this.countdown = this.countdownFn.bind(this);
    this.counter = this.counter.bind(this);

    this.countdown();
  }

  counter(){
    this.setState({
      isReset: false
    })

    var min = Math.floor(this.secondsRemaining / 60);
    var sec = this.secondsRemaining - (min * 60);

    this.setState({
      minutes: min,
      seconds: sec
    })

    if (sec < 10) {
      this.setState({
        seconds: "0" + this.state.seconds,
      })
    }
    if (min < 10) {
    this.setState({
      minutes: "0" + min,
     })
    }

    if (min === 0 & sec === 0) {
      this.setState({
        ...this.state,isReset:true
      })

      clearInterval(this.intervalHandle);

      this.setState({
        ...this.state,isMainTimerUsed:true
      })

    }

    this.secondsRemaining--
  }

  countdownFn() {
    let time = this.state.isMainTimerUsed?"03":this.state.minutes;
    this.secondsRemaining = time * 60;
    this.intervalHandle = setInterval(this.counter, 1000);
  }

  render(){
    return (
      <div>
        <span>Your OTP is expiring in {this.state.minutes}:{this.state.seconds}</span>
        <br />
        <button disabled={!this.state.isReset} onClick={this.startCountDown}>Resend OTP</button>
      </div>
    )
  }
}
