    this.tick = this.tick.bind(this);
  }

  change(event){
    this.set.State({
      value: event.target.value
    });
  }

  tick(){
    var min = Math.floor(this.secondsRemaining / 60);
    var sec = this.secondsRemaining - (min * 60);

    this.setState({
      minutes: min,
      seconds: sec
    })

    if (sec < 10) {
      this.setState({
        seconds: "0" + this.state.seconds,
      })
    }
    if (min < 10) {
