  }

  startCountDown() {
    this.intervalHandle = setInterval(this.tick, 1000);
    let time = this.state.minutes;
    this.secondsRemaining = time * 60;
    console.log(this.secondsRemaining);
    }

  render(){
    return (
      <div>
        <input type="text" value={this.state.value} onChange={(e)=>this.change} />
        <button type="button">{this.state.value}</button>
        <span>{this.state.minutes}:{this.state.seconds}</span>
        <button onClick={this.tick}>Start</button>
      </div>
    )
  }
}
