import React from 'react'


export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state={value:""}
  }

  change(event){
    this.set.State({
      value: event.target.value
    });
  }

  render(){
    return (
      <div>
        <input type="text" value={this.state.value} onChange={(e)=>this.change} />
        <button type="button">{this.state.value}</button>
      </div>
    )
  }
}
